package Clases; //LA CLASE PERSONA ES NUESTRA PLANTILLA, EN LA CUAL DEFINIMOS,CARACTERISTICAS O ATRIBUTOS Y TAMBIEN LOS METODOS O ACCIONES


public class PruebaPersona { //utilicamos el Pascal case(comienzan las dos con mayusculas) 
    public static void main(String[] args) { //El metodo main esta justamente para ejecutar el programa
        Persona persona1;  //vamos a comenzar con la creacion de un objeto, vamos hacerlo llamando a la clase persona 
        persona1 = new Persona();//LLAMAMOS AL CONTRUCTOR,ESTE CONTRUCTOR NOS PERMITE ASIGMAR VALORES AL OBJETO DE QUE LO CREAMOS,UNA VARIABLE SE ALMACENAEN UNA PARTE DIFERENTE,QUE 
        persona1.nombre = "Ariel";    //LOS OBJETOS EN LO QUE ES LA MEMORIA,POR ESO JAVA LOS ADMINISTRA DE MANERA DIFERENTE,EL CONTRUCTOR ES UN METODO ESPECIAL DONDE RESERBA MEMORIA PARA
        persona1.apellido = "Betancud"; //PODER CREAR OBJETOS,LO QUE HACE ES QUE LA CREAR EL OBJETO,EL CONTRUCTOR LE REGRESA LAS REFERENCIAS DONDE SE CREO EL OBJETO Y SE LO ASIGNA A LA VARIABLE
        persona1.obterInformacion(); //UNA VEZ ECHA ESTA CONECCION SE PUEDE ACCEDER A LOS ATRIBUTOS Y METODOS DE LA CLASE PERSONA EN ESTE CASO
        
        //CREAMOS UN NUEVO OBJETO
        Persona persona2 = new Persona();//null significa que tiene ausencia de valor, no tiene ningun valor cargado el objeto
        System.out.println("persona2 = " + persona2);
        System.out.println("persona1 = " + persona1);
        persona2.obterInformacion();//cada objeto tiene su propio informacion,los objetos no comparten informacion,lo Ãºnico que estan compartiendo son las caracteristicas de nombre y apellido
        persona2.nombre = "Osvaldo";                           //es lo que estan pidiendo cada objeto, es lo unico que estan compartiendo, que son los atributos que hemos definido ya en la clase
        persona2.apellido = "Giordanini";                                  
        persona2.obterInformacion();
    }
}
